import React from 'react';
import {
  BrowserRouter as Router,
  Route,
  Switch
} from 'react-router-dom';
import {Button} from 'react-bootstrap';
import { Link, withRouter } from "react-router-dom";

class Teacher extends React.Component {

  constructor() {
    super();
    this.state = {
      fields: {},
      errors: {}
    }

    this.handleChange = this.handleChange.bind(this);
    this.submituserStoryForm = this.submituserStoryForm.bind(this);

  };

  handleChange(e) {
    let fields = this.state.fields;
    fields[e.target.name] = e.target.value;
    this.setState({
      fields
    });

  }

  submituserStoryForm(e) {
    e.preventDefault();
    if (this.validateForm()) {
        let fields = {};
        fields["title"] = "";
        fields["content"] = "";
        this.setState({fields:fields});
        console.log(fields);
        alert("Story submitted");
    }

  }

  validateForm() {

    let fields = this.state.fields;
    console.log(JSON.stringify(fields));
    let errors = {};
    let formIsValid = true;

    if (!fields["title"]) {
      formIsValid = false;
      errors["title"] = "*Please enter a title for the story.";
    }

    if (!fields["content"]) {
      formIsValid = false;
      errors["content"] = "*Please enter content for the story.";
    }

    this.setState({
      errors: errors
    });
    return formIsValid;
  }  


  render() {
    return(
      <div>
        <div className="container" style={{marginTop: '30px'}}>
          <div className="row">
            <div className="col-sm-4">
              <ul className="nav nav-pills flex-column">
                <li className="nav-item">
                  <a className="nav-link active" href="#" onclick={this.StoryOptions}>Story Book</a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#">Picture Story</a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#">HomeWork</a>
                </li>
             </ul>
              <hr className="d-sm-none" />
            </div>
            <div className="col-sm-8" id="content">
              <h2>Create A New Story</h2>
              <form onSubmit={this.handleSubmit}>
              <label>
                Title:
                <input type="text" value={this.state.value} onChange={this.handleChange} />
              </label>
                <label>
                  Story
                  <textarea value={this.state.value} onChange={this.handleChange} />
                </label>
                <input type="submit" value="Submit" />
              </form>
            </div>
          </div>
        </div>
        <div className="jumbotron text-center" style={{marginBottom: 0}}>
          <p>Copyright. KidZania 2019</p>
        </div>
      </div>)
   }
}

export default withRouter(Teacher);
