import React from 'react';
import {
  BrowserRouter as Router,
  Link,
  Route,
  Switch
} from 'react-router-dom';
import {Button} from 'react-bootstrap';

class Teacher extends React.Component {

  saveStory = async event => {
    event.preventDefault();

    // Form validation
    

    // AWS and Database integration here
    const { title, content } = this.state;
    try
    {
      alert("Story Saved")
      this.props.history.push("/");
    } 
    catch
    {
      alert("Error Saving Story");
    }
  }
  
  render() {
    return(
      <div>
        <div className="container" style={{marginTop: '30px'}}>
          <div className="row">
            <div className="col-sm-4">
              <ul className="nav nav-pills flex-column">
                <li className="nav-item">
                  <a className="nav-link active" href="#" onclick={this.StoryOptions}>Story Book</a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#">Picture Story</a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#">HomeWork</a>
                </li>
             </ul>
              <hr className="d-sm-none" />
            </div>
            <div className="col-sm-8" id="content">
              <h2>Create A New Story</h2>
              <form>
                <div class="form-group">
                  <input class="form-control form-control-lg" type="text" placeholder="Story Title" id="title"></input>
                </div>
                <div class="form-group">
                  <textarea id="content" class="md-textarea form-control" rows="8" placeholder="Story Content"></textarea>
                </div>
                <button type="submit" class="btn btn-primary" onClick={this.saveStory}>Add Story</button>
              </form>
            </div>
          </div>
        </div>
        <div className="jumbotron text-center" style={{marginBottom: 0}}>
          <p>Copyright. KidZania 2019</p>
        </div>
      </div>)
   }
}

export default Teacher;
