import React from 'react';
import {
  BrowserRouter as Router,
  Route,
  Switch
} from 'react-router-dom';
import {Button} from 'react-bootstrap';
import { Link, withRouter } from "react-router-dom";

class Teacher extends React.Component {

  constructor() {
    super();
    this.state = {
      fields: {},
      errors: {}
    }

    this.onInputChange = this.onInputChange.bind(this);
    this.submituserStory = this.submituserStory.bind(this);

  };

  onInputChange = event => {
    this.setState({
      [event.target.id]: event.target.value
    });
    document.getElementById(event.target.id).classList.remove("is-danger");
  }

  submituserStory(e) {
    e.preventDefault();
    if (this.validateForm()) {
        let fields = {};
        fields["title"] = "";
        fields["content"] = "";
        this.setState({fields:fields});
        console.log(fields);
        alert("Story submitted");
    }

  }

  validateForm() {

    let fields = this.state.fields;
    let errors = {};
    let formIsValid = true;

    if (!fields["title"]) {
      formIsValid = false;
      errors["title"] = "*Please enter a title for the story.";
    }

    if (!fields["content"]) {
      formIsValid = false;
      errors["content"] = "*Please enter content for the story.";
    }

    this.setState({
      errors: errors
    });
    return formIsValid;
  }  


  render() {
    return(
      <div>
        <div className="container" style={{marginTop: '30px'}}>
          <div className="row">
            <div className="col-sm-4">
              <ul className="nav nav-pills flex-column">
                <li className="nav-item">
                  <a className="nav-link active" href="#" onclick={this.StoryOptions}>Story Book</a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#">Picture Story</a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#">HomeWork</a>
                </li>
             </ul>
              <hr className="d-sm-none" />
            </div>
            <div className="col-sm-8" id="content">
              <h2>Create A New Story</h2>
              <form name="userStory"  onSubmit={this.submituserStory} >
                <div class="form-group">
                  <input class="form-control form-control-lg" 
                    type="text" placeholder="Story Title" 
                    id="title" value={this.state.title} 
                    onChange={this.onInputChange}>
                  </input>
                  <div className="errorMsg">{this.state.errors.title}</div>
                </div>
                <div class="form-group">
                <input class="form-control form-control-lg" 
                    type="text" placeholder="Story Content" 
                    id="content" value={this.state.content} 
                    onChange={this.onInputChange}>
                  </input>
                  <div className="errorMsg">{this.state.errors.content}</div>
                </div>
                <input type="submit" className="button"  value="Register"/>
              </form>
            </div>
          </div>
        </div>
        <div className="jumbotron text-center" style={{marginBottom: 0}}>
          <p>Copyright. KidZania 2019</p>
        </div>
      </div>)
   }
}

export default withRouter(Teacher);
